import React from 'react';
import './chat.scss';

let UserInput = () => {
    //   form / onSubmit -- run the onInput function :)
    return (
        <>
        <form onSubmit={this.props.onInput} className="input-container">
        <input id="chat" type="text" ref={msg => this.msg = msg} placeholder="Your message here."/>
        <button type="submit" className="input-submit" />
        </form>
        </>
    );
};

  export default UserInput;
