import React from 'react';
import './chat.scss';

let UserInput = (props) => {
    //   form / onSubmit -- run the onInput function :)
    return (
        <>
            <form onSubmit={props.onInput} className="input-container">
                <input id="chat" type="text" name="message" placeholder="Your message here."/>
                <button type="submit" name="submit" className="input-submit" />
            </form>
        </>
    );
};

  export default UserInput;
