import React from "react";
import axios from 'axios';
import SpotifyWebApi from "spotify-web-api-js";
const key = 'f4858bfd2ff200f8aa00968b994de933';
const spotifyApi = new SpotifyWebApi();
import Genius from 'genius-api';
const genius = new Genius(accessToken);
const accessToken = '9Kn10cuCojvjcfh0adnSR0tFYxSgV3qKst5cdNG2VESwLYLfrEoEk1oZ5dwemsPT';
//const api = require('genius-api');

class Player extends React.Component {
    //https://medium.com/@jonnykalambay/now-playing-using-spotifys-awesome-api-with-react-7db8173a7b13
    constructor(){
      super();
      const params = this.getHashParams();
      //console.log(params);
      const token = params.access_token;
      if (token) {
        spotifyApi.setAccessToken(token);
      }
      this.state = {
        loggedIn: token ? true : false,
        nowPlaying: { name: 'Not Checked', albumArt: '' }
      }
    }
    getHashParams() {
      var hashParams = {};
      var e, r = /([^&;=]+)=?([^&;]*)/g,
          q = window.location.hash.substring(1);
      e = r.exec(q)
      while (e) {
         hashParams[e[1]] = decodeURIComponent(e[2]);
         e = r.exec(q);
      }
      return hashParams; //returns user's access token
    }
    getNowPlaying(){
      spotifyApi.getMyCurrentPlaybackState()
        .then((response) => {
          console.log(response);
          this.setState({
            nowPlaying: { 
                name: response.item.name, 
                albumArt: response.item.album.images[0].url
              }
          });
        })
    }

	componentDidMount() {
		axios({
			baseURL: 'https://api.spotify.com/v1/tracks/11dFghVXANMlKmJXsNCbNl',
			auth: { username: 'f5e8e6f86fdd4361a33a3daaa5bcf808', password: '71a41656726a455a9cc526e9cbf2b168'}
			}).then(res => console.log(res))
			.catch(error=>{
				console.log(error)
			})

		axios.get(`http://api.musixmatch.com/ws/1.1/track.search?q_lyrics=hello&page_size=3&page=1&s_track_rating=desc&${key}`)
		.then(res=>{
			console.log(res)
		})
		.catch(error=>{
			console.log(error)
    })
    genius.search('Hello')
    .then(function(response) {
    console.log('hits', response.hits);
  });
	}
    render() {
      return (
            <div className="Player">
                <a href='http://localhost:8888' > Login to Spotify </a>
                <div>
                    Now Playing: { this.state.nowPlaying.name }
                </div>
                <div>
                    <img src={this.state.nowPlaying.albumArt} style={{ height: 150 }} alt="album cover"/>
                  </div>
                { this.state.loggedIn &&
                <button onClick={() => this.getNowPlaying()}>
                    Check Now Playing
                </button>
                }
                <iframe src="https://open.spotify.com/embed/album/1DFixLWuPkv3KT3TnV35m3" width="300" height="380" frameborder="0" allowtransparency="true" allow="encrypted-media"></iframe>
          </div>
        );
  }
  }

  export default Player;
